def factorial(n):
 if n == 0:
 return 1
 else:
 return n * factorial(n-1)

function getRandomInt(min, max) {
 min = Math.ceil(min);
 max = Math.floor(max);
 return Math.floor(Math.random() * (max - min + 1)) + min;
}

